mapper.insert:
insert into dms_device_inbound_detail
(inbound_code, device_code, device_name, device_model, device_spec, device_brand, inbound_number, inbound_date)
values
(#{inboundDetail.inboundCode}, #{inboundDetail.deviceCode}, #{inboundDetail.deviceName}, #{inboundDetail.deviceModel},
 #{inboundDetail.deviceSpec}, #{inboundDetail.deviceBrand}, #{inboundDetail.inboundNumber}, #{inboundDetail.inboundDate}
)

mapper.select:
SELECT  id, inbound_code, inbound_name, inbound_agent, inbound_date, inbound_remark
FROM dms_device_inbound
WHERE id = #{id}

SELECT  id, inbound_code, device_code, device_name, device_model, device_spec, device_brand, inbound_number, inbound_date
FROM dms_device_inbound_detail
WHERE inbound_code = #{inboundCode}

mapper.update:
UPDATE dms_device_inbound SET
  inbound_name = #{deviceInbound.inboundName},
  inbound_agent = #{deviceInbound.inboundAgent},
  inbound_date = #{deviceInbound.inboundDate},
  inbound_remark = #{deviceInbound.inboundRemark}
WHERE id = #{deviceInbound.id}
