mapper.insert:
insert into dms_device_install_detail
(install_code, device_code, device_name, device_model, device_spec, device_brand, install_number, install_date)
values
(#{installDetail.installCode}, #{installDetail.deviceCode}, #{installDetail.deviceName}, #{installDetail.deviceModel},
 #{installDetail.deviceSpec}, #{installDetail.deviceBrand}, #{installDetail.installNumber}, #{installDetail.installDate}
)

mapper.select:
SELECT  id, install_code, install_name, install_agent, install_date, install_remark
FROM dms_device_install
WHERE id = #{id}

SELECT  id, install_code, device_code, device_name, device_model, device_spec, device_brand, install_number, install_date
FROM dms_device_install_detail
WHERE install_code = #{installCode}

mapper.update:
UPDATE dms_device_install SET
  install_name = #{deviceInstall.installName},
  install_agent = #{deviceInstall.installAgent},
  install_date = #{deviceInstall.installDate},
  install_remark = #{deviceInstall.installRemark}
WHERE id = #{deviceInstall.id}
