mapper.insert:
insert into dms_device_repair_detail
(repair_code, device_code, device_name, device_model, device_spec, device_brand, repair_number, repair_date)
values
(#{repairDetail.repairCode}, #{repairDetail.deviceCode}, #{repairDetail.deviceName}, #{repairDetail.deviceModel},
 #{repairDetail.deviceSpec}, #{repairDetail.deviceBrand}, #{repairDetail.repairNumber}, #{repairDetail.repairDate}
)

mapper.select:
SELECT  id, repair_code, repair_name, repair_agent, repair_date, repair_remark
FROM dms_device_repair
WHERE id = #{id}

SELECT  id, repair_code, device_code, device_name, device_model, device_spec, device_brand, repair_number, repair_date
FROM dms_device_repair_detail
WHERE repair_code = #{repairCode}

mapper.update:
UPDATE dms_device_repair SET
  repair_name = #{deviceRepair.repairName},
  repair_agent = #{deviceRepair.repairAgent},
  repair_date = #{deviceRepair.repairDate},
  repair_remark = #{deviceRepair.repairRemark}
WHERE id = #{deviceRepair.id}
