mapper.select:
SELECT  id, scrap_code, device_code, scrap_reason, scrap_date
FROM dms_device_scrap
WHERE id = #{id}

mapper.update:
UPDATE dms_device_scrap SET
  scrap_name = #{deviceScrap.scrapName},
  scrap_agent = #{deviceScrap.scrapAgent},
  scrap_date = #{deviceScrap.scrapDate},
  scrap_remark = #{deviceScrap.scrapRemark}
WHERE id = #{deviceScrap.id}
