mapper.select:
SELECT COUNT(1) FROM pps_patrol_point_detail t
WHERE 1 = 1

    AND t.point_id = #{patrolPointDetail.pointId}

mapper.update:
UPDATE pps_patrol_point_detail SET
PointDetail_name = #{patrolPointDetail.PointDetailName},
PointDetail_desc = #{patrolPointDetail.PointDetailDesc},
coordinate_x = #{patrolPointDetail.coordinateX},
coordinate_x = #{patrolPointDetail.coordinateY}
WHERE id = #{patrolPointDetail.id}
